mapper.select:
select DISTINCT ins.userId,e.id,e.mid,e.taskId,t.coding as taskcoding,t.name as taskName,e.workBills,t.id,t.taskMan xiafaid, u.name as xiafaMan,t.date as xiafaDate,p.settingName as taskStatus,t.actualDate as wancdate,t.delete_flag,t.type,t.predictDate,t.remark,t.delete_flag as flag from t_eliminate e left join t_flawstaff ins on e.id=ins.eliminateId left join t_task t on e.taskId=t.id left join t_user u on t.taskMan=u.id left join t_systemparam p on t.state=p.id where t.type='消缺任务'
and t.state in (SELECT id FROM t_systemparam where settingid = (select id FROM t_systemsetting WHERE coding='Task_STATE' AND delete_flag='否' AND state='启用') )
AND t.taskMan=#{userid} or e.mid=#{userid}
and ins.userId=#{userid}
and ins.isReceipter='是'
and t.coding like #{taskcoding}
and e.workBills like #{workbills}
and p.settingName like #{taskstatus}
and u.name like #{xiapeople}
and t.date > #{startdate}
and t.date < #{enddate}
ORDER BY t.date desc limit #{pageIndex},#{pageSize}

select count(1)
from t_eliminate e left join t_flawstaff ins on
e.id=ins.eliminateId
left join t_task t on e.taskId=t.id
left join
t_user u on t.taskMan=u.id
left join t_systemparam p on t.state=p.id
where t.type='消缺任务'


	and t.state in (SELECT id FROM t_systemparam where
	settingid
	= (select
	id FROM t_systemsetting
	WHERE coding='Task_STATE'
	AND
	delete_flag='否'
	AND state='启用'))
	
		AND e.mid=#{userid}
	
	
	
		and ins.userId=#{userid}
	
	and ins.isReceipter='是'


	and t.coding like #{taskcoding}


	and e.workBills like #{workbills}


	and p.settingName like #{taskstatus}


	and u.name like #{xiapeople}


	and t.date >= #{startdate}


	and t.date <= #{enddate}

select DISTINCT ins.userId,e.id,e.mid,e.taskId,t.coding as taskcoding,t.name as taskName,e.workBills,t.id,t.taskMan xiafaid, u.name as xiafaMan,t.date as xiafaDate,p.settingName as taskStatus,t.actualDate as wancdate,t.delete_flag,t.type,t.predictDate,t.remark,t.delete_flag as flag from t_eliminate e left join t_flawstaff ins on e.id=ins.eliminateId left join t_task t on e.taskId=t.id left join t_user u on t.taskMan=u.id left join t_systemparam p on t.state=p.id where t.type='消缺任务'
and t.state not in (SELECT id FROM t_systemparam where settingid = (select id FROM t_systemsetting WHERE coding='Task_STATE' AND delete_flag='否' AND state='启用') AND settingName='待分配' OR settingName='已取消')
AND e.mid=#{userid}
and ins.userId=#{userid}
and ins.isReceipter='是'
and t.coding like #{receipt_taskcoding}
and e.workBills = #{receipt_bills}
and u.name like #{receipt_xiapeople}
and t.date >= #{receipt_startdate}
and t.date <= #{receipt_enddate}
ORDER BY t.date desc limit #{pageIndex},#{pageSize}

select count(1) from t_eliminate e left join t_flawstaff ins on e.id=ins.eliminateId left join t_task t on e.taskId=t.id left join t_user u on t.taskMan=u.id left join t_systemparam p on t.state=p.id where t.type='消缺任务'
and t.state not in (SELECT id FROM t_systemparam where settingid = (select id FROM t_systemsetting WHERE coding='Task_STATE' AND delete_flag='否' AND state='启用') AND settingName='待分配' OR settingName='已取消')
AND e.mid=#{userid}
and ins.userId=#{userid}
and ins.isReceipter='是'
and t.coding like #{receipt_taskcoding}
and e.workBills = #{receipt_bills}
and u.name like #{receipt_xiapeople}
and t.date >= #{receipt_startdate}
and t.date <= #{receipt_enddate}
