mapper.select:

	SELECT ins.threadId as id,coding,th.name FROM t_inspection ins
	left JOIN t_thread th on ins.threadId=th.id
	 WHERE ins.taskId=#{id}

SELECT DISTINCT t.id,t.coding,t.name,th.name as thread,th.startTower,th.endTower,u.userName as creater,t.date as createDate,sy.settingName as state,t.actualDate,t.delete_flag from t_inspection i left JOIN t_inspectionstaff ins ON i.id=ins.inspectionId left JOIN t_task t ON i.taskId=t.id left JOIN t_thread th ON i.threadId=th.id left JOIN t_user u on t.createdBy=u.id left JOIN t_systemparam sy on t.state=sy.id WHERE t.type='巡检任务'
and t.state not in (SELECT id FROM t_systemparam where settingid = (select id FROM t_systemsetting WHERE coding='inspection_state' AND delete_flag='否' AND state='启用') AND settingName='待分配' OR settingName='已取消') AND ins.userId=#{userId} AND ins.isReceipter='是'
ORDER BY t.createdDate LIMIT #{pageIndex},#{pageSize}
