mapper.select:
        select count(1) from t_role where delete_flag='否'
         
			and	name LIKE '%${name}%'
		
		
			and state = #{state}

select tr.id,tr.coding,tr.`name`,tr.state,tr.createBy,tu.`name` as createName,tr.createDate,tr.updatedBy,tr.updatedDate from t_role tr LEFT JOIN t_user tu on tu.id=tr.createBy where tr.delete_flag='否'
and tr.name LIKE '%${name}%'
and tr.state = #{state}
GROUP BY tr.id order by tr.id desc limit #{pageIndex},#{pageSize}

        select id,coding,name,state,createBy,updatedDate from t_role 
         
        
			name LIKE '%${make}%'
		
		
			state = #{state}
