mapper.select:
SELECT distinct a.coding tcoding,a.`name` tename,h.coding ethreadCoding,h.startTower estartTower, h.endTower tendTower FROM t_flawconfirm f,t_eliminate_record r,t_eliminate e,t_task a,t_thread h,t_tower w,t_systemparam s,t_flaw wa WHERE f.id=r.yetid AND r.xtaskid=e.taskId AND r.xtaskid=a.id AND h.id=f.threadId AND wa.id=f.flawId AND f.towerId=w.id AND s.id=f.flawGrade AND a.actualDate is NOT NULL AND f.confirmstate=0
AND a.coding LIKE #{rent}
AND h.coding LIKE #{cont}
limit #{pageIndex},#{pageSize}

SELECT w.coding etowerCoding,f.isTrouble eisTrouble,s.settingName esettingName ,wa.`name` eflawTname,a.actualDate eactualDate,f.discoverDate ediscoverDate,f.flawDesc eflawDesc FROM t_flawconfirm f,t_eliminate_record r,t_eliminate e,t_task a,t_thread h,t_tower w,t_systemparam s,t_flaw wa WHERE f.id=r.yetid AND r.xtaskid=e.taskId AND r.xtaskid=a.id AND h.id=f.threadId AND wa.id=f.flawId AND f.towerId=w.id AND s.id=f.flawGrade AND a.actualDate is NOT NULL AND a.coding=#{tcoding} AND f.confirmstate=0
AND w.coding LIKE #{gant}
AND s.settingName LIKE #{quex}
AND f.isTrouble LIKE #{ist}
AND a.actualDate >= #{start}
AND a.actualDate <= #{entt}
ORDER BY a.actualDate DESC LIMIT #{pageIndex},#{pageSize}
